        if(img_src == "http://localhost/ligomoku/ligomoku/public/images/icons/w_open.png") {
            //alert("is checked!");
            $( "#check" ).prop( "checked", false );
            $('#checking').attr('src',"http://localhost/ligomoku/ligomoku/public/images/icons/w_close.png");
        }
        else{
            //alert("is NOT checked!");
            $( "#check" ).prop( "checked", true );
            $('#checking').attr('src',"http://localhost/ligomoku/ligomoku/public/images/icons/w_open.png");
        }
    });
</script>

 